mapper.select:
SELECT Tab.*
FROM (
SELECT T.*,ROW_NUMBER() OVER(ORDER BY T.ID DESC) AS NUM from ADS_CHECK_INFO T
where 1 = 1 AND T.DEL_FLAG != 'N'
)Tab
WHERE Tab.NUM BETWEEN 1+#{pageSize}*(#{pageNum}-1) AND #{pageSize}*#{pageNum}

SELECT COUNT(1) FROM ads_monitor_sample a, ads_check_info b, ads_organ_task c, ads_monitor_task d where 1 = 1 and b.ORGAN_TASK_ID = c.id and c.MONITOR_TASK_ID = d.id AND b.DEL_FLAG != 'Y' and a.id = b.MONITOR_SAMPLE_ID and d.MONITOR_CLASS=#{monitorClass}
AND d.YEAR = #{year}
AND d.TASK_NAME=#{taskName}
AND lower(b.PRODUCT_TRACEABILITY_CODE ) like '%' || lower(trim(#{productTraceabilityCode})) ||'%'
AND lower(b.SAMPLE_CODE) like '%' || lower(trim(#{sampleCode})) ||'%'
AND lower(b.SAMPLE_NAME) like '%' || lower(trim(#{sampleName})) ||'%'
AND b.RESULT = #{result}
AND lower(a.SAMPLE_DEPARMENT) like '%' || lower(trim(#{sampleDeparment})) ||'%'
AND b.CHECK_LINK=#{checkLink}
AND lower(b.CHECK_ORGAN) like '%' || lower(trim(#{checkOrgan})) ||'%'
AND b.CHECK_TIME >=to_date(#{startTime},'yyyy-MM-dd')
AND b.CHECK_TIME <=to_date(#{endTime},'yyyy-MM-dd')+1
AND lower(a.TESTED_DEPARMENT) like '%' || lower(trim(#{testedDeparment})) ||'%'
AND lower(a.PRODUCING_AREA) like '%' || lower(trim(#{producingArea})) ||'%'
AND lower(c.CITY_CODE) like '%' || lower(trim(#{cityCode})) ||'%'
AND d.ORGAN_ID = #{checkOrganId}

SELECT COUNT(1) FROM ads_monitor_sample a, ads_check_info b, ads_organ_task c, ads_monitor_task d where 1 = 1 AND b.DEL_FLAG != 'Y' and b.ORGAN_TASK_ID = c.id and c.MONITOR_TASK_ID = d.id and a.id = b.MONITOR_SAMPLE_ID and d.MONITOR_CLASS=#{monitorClass} and (c.SAMPLE_ORGAN_ID=#{orgId} or c.detection_organ_id=#{orgId})
AND d.YEAR = #{year}
AND d.TASK_NAME=#{taskName}
AND lower(b.PRODUCT_TRACEABILITY_CODE ) like '%' || lower(trim(#{productTraceabilityCode})) ||'%'
AND lower(b.SAMPLE_CODE) like '%' || lower(trim(#{sampleCode})) ||'%'
AND b.SAMPLE_NAME = #{sampleName}
AND b.RESULT = #{result}
AND a.SAMPLE_DEPARMENT = #{sampleDeparment}
AND lower(b.CHECK_LINK) like '%' || lower(trim(#{checkLink})) ||'%'
AND lower(b.CHECK_ORGAN) like '%' || lower(trim(#{checkOrgan})) ||'%'
AND b.CHECK_TIME >=to_date(#{startTime},'yyyy-MM-dd')
AND b.CHECK_TIME <=to_date(#{endTime},'yyyy-MM-dd')+1
AND lower(a.TESTED_DEPARMENT) like '%' || lower(trim(#{testedDeparment})) ||'%'
AND lower(a.PRODUCING_AREA) like '%' || lower(trim(#{producingArea})) ||'%'
AND lower(c.CITY_CODE) like '%' || lower(trim(#{cityCode})) ||'%'

select t.* from (select tmp.*, rownum rn from (select a.TESTED_DEPARMENT, a.SAMPLE_PLACE, a.SAMPLE_DEPARMENT, a.producing_area, TO_CHAR(a.SAMPLE_DATE, 'yyyy-mm-dd') SAMPLE_DATE, b.RESULT, b.MONITOR_SAMPLE_ID, b.CHECK_ORGAN, b.PRODUCT_TRACEABILITY_CODE, b.SAMPLE_CODE, b.SAMPLE_NAME, b.CHECK_LINK, b.CHECK_TIME, c.MONITOR_TASK_ID, c.sample_organ_id, d.TASK_NAME, d.YEAR, e.check_project, e.check_num, e.judge_num from ads_monitor_sample a, ads_check_info b, ads_organ_task c, ads_monitor_task d, Ads_Info_Project e where 1 = 1 AND b.DEL_FLAG != 'Y' and b.ORGAN_TASK_ID = c.id and c.MONITOR_TASK_ID = d.id and a.id = b.MONITOR_SAMPLE_ID and e.check_Info_Id=b.id and d.MONITOR_CLASS=#{monitorClass}
AND d.YEAR = #{year}
AND d.TASK_NAME=#{taskName}
AND lower(b.PRODUCT_TRACEABILITY_CODE ) like '%' || lower(trim(#{productTraceabilityCode})) ||'%'
AND lower(b.SAMPLE_CODE) like '%' || lower(trim(#{sampleCode})) ||'%'
AND b.SAMPLE_NAME = #{sampleName}
AND b.RESULT = #{result}
AND a.SAMPLE_DEPARMENT = #{sampleDeparment}
AND lower(b.CHECK_LINK) like '%' || lower(trim(#{checkLink})) ||'%'
AND b.CHECK_ORGAN = #{checkOrgan}
AND a.SAMPLE_DATE >=to_date(#{startTime},'yyyy-MM-dd')
AND a.SAMPLE_DATE <=to_date(#{endTime},'yyyy-MM-dd')
AND a.TESTED_DEPARMENT = #{testedDeparment}
AND lower(a.PRODUCING_AREA) like '%' || lower(trim(#{producingArea})) ||'%'
AND lower(c.CITY_CODE) like '%' || lower(trim(#{cityCode})) ||'%'
order by b.update_time desc, b.create_time desc,b.id desc) tmp
where rownum <= #{pageSize}*#{pageNum}) t where t.rn >= 1+#{pageSize}*(#{pageNum}-1)

select t.* from (select tmp.*, rownum rn from (select a.TESTED_DEPARMENT, a.ID, a.SAMPLE_PLACE, a.SAMPLE_DEPARMENT, TO_CHAR(a.SAMPLE_DATE, 'yyyy-mm-dd') SAMPLE_DATE, b.RESULT, b.MONITOR_SAMPLE_ID, b.CHECK_ORGAN, b.PRODUCT_TRACEABILITY_CODE, b.SAMPLE_CODE, b.SAMPLE_NAME, b.CHECK_LINK, b.CHECK_TIME, c.MONITOR_TASK_ID, c.sample_organ_id, d.TASK_NAME, d.YEAR, d.publish_status from ads_monitor_sample a, ads_check_info b, ads_organ_task c, ads_monitor_task d where 1 = 1 AND b.DEL_FLAG != 'Y' and b.ORGAN_TASK_ID = c.id and c.MONITOR_TASK_ID = d.id and a.id = b.MONITOR_SAMPLE_ID and d.MONITOR_CLASS=#{monitorClass}
AND d.YEAR = #{year}
AND d.TASK_NAME=#{taskName}
AND lower(b.PRODUCT_TRACEABILITY_CODE ) like '%' || lower(trim(#{productTraceabilityCode})) ||'%'
AND lower(b.SAMPLE_CODE) like '%' || lower(trim(#{sampleCode})) ||'%'
AND lower(b.SAMPLE_NAME) like '%' || lower(trim(#{sampleName})) ||'%'
AND b.RESULT = #{result}
AND lower(a.SAMPLE_DEPARMENT) like '%' || lower(trim(#{sampleDeparment})) ||'%'
AND b.CHECK_LINK=#{checkLink}
AND lower(b.CHECK_ORGAN) like '%' || lower(trim(#{checkOrgan})) ||'%'
AND b.CHECK_TIME >=to_date(#{startTime},'yyyy-MM-dd')
AND b.CHECK_TIME <=to_date(#{endTime},'yyyy-MM-dd')+1
AND lower(a.TESTED_DEPARMENT) like '%' || lower(trim(#{testedDeparment})) ||'%'
AND lower(a.PRODUCING_AREA) like '%' || lower(trim(#{producingArea})) ||'%'
AND lower(c.CITY_CODE) like '%' || lower(trim(#{cityCode})) ||'%'
AND d.ORGAN_ID = #{checkOrganId}
order by b.update_time desc, b.create_time desc,b.id desc) tmp
where rownum <= #{pageSize}*#{pageNum}) t where t.rn >= 1+#{pageSize}*(#{pageNum}-1)

mapper.update:
update ADS_CHECK_INFO
set
RESULT =  #{result,jdbcType=VARCHAR}
where ID =#{id}

mapper.select:
select t.* from (select tmp.*, rownum rn from (select a.TESTED_DEPARMENT, a.SAMPLE_PLACE, a.SAMPLE_DEPARMENT, a.producing_area, TO_CHAR(a.SAMPLE_DATE, 'yyyy-mm-dd') SAMPLE_DATE, b.RESULT, b.MONITOR_SAMPLE_ID, b.CHECK_ORGAN, b.PRODUCT_TRACEABILITY_CODE, b.SAMPLE_CODE, b.SAMPLE_NAME, b.CHECK_LINK, b.CHECK_TIME, c.MONITOR_TASK_ID, c.sample_organ_id, d.TASK_NAME, d.YEAR, e.check_project, e.check_num, e.judge_num from ads_monitor_sample a, ads_check_info b, ads_organ_task c, ads_monitor_task d, Ads_Info_Project e where 1 = 1 AND b.DEL_FLAG != 'Y' and b.ORGAN_TASK_ID = c.id and c.MONITOR_TASK_ID = d.id and a.id = b.MONITOR_SAMPLE_ID and e.check_Info_Id=b.id and d.MONITOR_CLASS=#{monitorClass} and c.SAMPLE_ORGAN_ID=#{orgId} or c.detection_organ_id=#{orgId}
AND d.YEAR = #{year}
AND d.TASK_NAME=#{taskName}
AND lower(b.PRODUCT_TRACEABILITY_CODE ) like '%' || lower(trim(#{productTraceabilityCode})) ||'%'
AND lower(b.SAMPLE_CODE) like '%' || lower(trim(#{sampleCode})) ||'%'
AND b.SAMPLE_NAME = #{sampleName}
AND b.RESULT = #{result}
AND a.SAMPLE_DEPARMENT = #{sampleDeparment}
AND lower(b.CHECK_LINK) like '%' || lower(trim(#{checkLink})) ||'%'
AND b.CHECK_ORGAN = #{checkOrgan}
AND a.SAMPLE_DATE >=to_date(#{startTime},'yyyy-MM-dd')
AND a.SAMPLE_DATE <=to_date(#{endTime},'yyyy-MM-dd')
AND a.TESTED_DEPARMENT = #{testedDeparment}
AND lower(a.PRODUCING_AREA) like '%' || lower(trim(#{producingArea})) ||'%'
AND lower(c.CITY_CODE) like '%' || lower(trim(#{cityCode})) ||'%'
order by b.update_time desc, b.create_time desc,b.id desc) tmp
where rownum <= #{pageSize}*#{pageNum}) t where t.rn >= 1+#{pageSize}*(#{pageNum}-1)

select rownum rn, a.TESTED_DEPARMENT, a.SAMPLE_PLACE, a.SAMPLE_DEPARMENT, TO_CHAR(a.SAMPLE_DATE, 'yyyy-mm-dd') SAMPLE_DATE, b.RESULT, b.ORGAN_TASK_ID, b.MONITOR_SAMPLE_ID, b.CHECK_ORGAN, b.PRODUCT_TRACEABILITY_CODE, b.SAMPLE_CODE, b.SAMPLE_NAME, b.CHECK_LINK, b.CHECK_TIME, c.MONITOR_TASK_ID, c.sample_organ_id, d.TASK_NAME, d.YEAR from ads_monitor_sample a, ads_check_info b, ads_organ_task c, ads_monitor_task d where 1 = 1 AND b.DEL_FLAG != 'Y' and b.ORGAN_TASK_ID = c.id and c.MONITOR_TASK_ID = d.id and a.id = b.MONITOR_SAMPLE_ID and d.MONITOR_CLASS=#{monitorClass}
AND d.YEAR = #{year}
AND d.TASK_NAME=#{taskName}
AND lower(b.PRODUCT_TRACEABILITY_CODE ) like '%' || lower(trim(#{productTraceabilityCode})) ||'%'
AND lower(b.SAMPLE_CODE) like '%' || lower(trim(#{sampleCode})) ||'%'
AND lower(b.SAMPLE_NAME) like '%' || lower(trim(#{sampleName})) ||'%'
AND b.RESULT = #{result}
AND lower(a.SAMPLE_DEPARMENT) like '%' || lower(trim(#{sampleDeparment})) ||'%'
AND b.CHECK_LINK=#{checkLink}
AND lower(b.CHECK_ORGAN) like '%' || lower(trim(#{checkOrgan})) ||'%'
AND b.CHECK_TIME >=to_date(#{startTime},'yyyy-MM-dd')
AND b.CHECK_TIME <=to_date(#{endTime},'yyyy-MM-dd')+1
