mapper.select:
SELECT Tab.*
FROM (
SELECT T.*,ROW_NUMBER() OVER(ORDER BY T.CREATE_TIME DESC) AS NUM from TTS_SCLTXXCJ_COMPLAINT_V2 T
where 1 = 1 AND T.DEL_FLAG != 'Y'

    and T.USER_IDCODE = #{bean.userIdcode}


    and T.ENTITY_IDCODE = #{bean.entityIdcode}


    and T.BE_ENTITY_IDCODE = #{bean.beEntityIdcode}


    and T.BE_USER_IDCODE = #{bean.beUserIdcode}


    and T.ACC_ENTITY_IDCODE = #{bean.accEntityIdcode}


    and T.ACC_USER_IDCODE = #{bean.accUserIdcode}


    and UPPER(T.COMPLAINT_TITLE) LIKE UPPER('%${bean.complaintTitle}%')


    and UPPER(T.CONTENT) LIKE UPPER('%${bean.content}%')


    and T.TYPE = #{bean.type}


    and T.STATUS = #{bean.status}


    and UPPER(T.ACCEPTANCE) LIKE UPPER('%${bean.acceptance}%')


    and T.AREA_ID = #{bean.areaId}


    and UPPER(T.COMPLAINT_ENT_NAME) LIKE UPPER('%${bean.complaintEntName}%')


    and UPPER(T.COMPLAINT_COP_NAME) LIKE UPPER('%${bean.complaintCopName}%')


    and ( UPPER(T.COMPLAINT_TITLE) LIKE UPPER('%'||#{keywords}||'%') or  UPPER(T.COMPLAINT_ENT_NAME) LIKE UPPER('%'||#{keywords}||'%') )


    and to_char(T.CREATE_TIME,'yyyy-MM-dd') >= #{dateBegin}


    and to_char(T.CREATE_TIME,'yyyy-MM-dd') <= #{dateEnd}

)Tab
WHERE Tab.NUM BETWEEN 1+#{pageSize}*(#{pageNum}-1) AND #{pageSize}*#{pageNum}

SELECT COUNT(*) FROM TTS_SCLTXXCJ_COMPLAINT_V2 T
where 1 = 1 AND T.DEL_FLAG != 'Y'

    and T.USER_IDCODE = #{bean.userIdcode}


    and T.ENTITY_IDCODE = #{bean.entityIdcode}


    and T.BE_ENTITY_IDCODE = #{bean.beEntityIdcode}


    and T.BE_USER_IDCODE = #{bean.beUserIdcode}


    and T.ACC_ENTITY_IDCODE = #{bean.accEntityIdcode}


    and T.ACC_USER_IDCODE = #{bean.accUserIdcode}


    and UPPER(T.COMPLAINT_TITLE) LIKE UPPER('%${bean.complaintTitle}%')


    and UPPER(T.CONTENT) LIKE UPPER('%${bean.content}%')


    and T.TYPE = #{bean.type}


    and T.STATUS = #{bean.status}


    and UPPER(T.ACCEPTANCE) LIKE UPPER('%${bean.acceptance}%')


    and T.AREA_ID = #{bean.areaId}


    and UPPER(T.COMPLAINT_ENT_NAME) LIKE UPPER('%${bean.complaintEntName}%')


    and UPPER(T.COMPLAINT_COP_NAME) LIKE UPPER('%${bean.complaintCopName}%')


    and ( UPPER(T.COMPLAINT_TITLE) LIKE UPPER('%'||#{keywords}||'%') or  UPPER(T.COMPLAINT_ENT_NAME) LIKE UPPER('%'||#{keywords}||'%') )


    and to_char(T.CREATE_TIME,'yyyy-MM-dd') >= #{dateBegin}


    and to_char(T.CREATE_TIME,'yyyy-MM-dd') <= #{dateEnd}

SELECT Tab.*
FROM (
SELECT T.*,ROW_NUMBER() OVER(ORDER BY T.ID DESC) AS NUM from TTS_SCLTXXCJ_REGISTER_V2 T
where 1 = 1 AND T.DEL_FLAG != 'Y'

    and UPPER(T.ENTERPRISE_NAME) LIKE UPPER('%'||#{keywords}||'%')


    and id != #{id}

)Tab
WHERE Tab.NUM BETWEEN 1+#{pageSize}*(#{pageNum}-1) AND #{pageSize}*#{pageNum}

SELECT COUNT(*) FROM TTS_SCLTXXCJ_REGISTER_V2 T where 1 = 1 AND T.DEL_FLAG != 'Y'
and UPPER(T.ENTERPRISE_NAME) LIKE UPPER('%'||#{keywords}||'%')
and id != #{id}

select * from ( SELECT f.*,ROWNUM AS rn FROM ( select t.*, (select t2.AREA from TTS_SCLTXXCJ_REGISTER_V2 t2 where t.be_entity_idcode = t2.entity_idcode) as be_area_id, (select t2.enterprise_name from TTS_SCLTXXCJ_REGISTER_V2 t2 where t.entity_idcode = t2.entity_idcode) as enti_name, (select t2.enterprise_name from TTS_SCLTXXCJ_REGISTER_V2 t2 where t.be_entity_idcode = t2.entity_idcode) as be_enti_name from tts_scltxxcj_complaint_V2 t,TTS_SCLTXXCJ_REGISTER_V2 tt WHERE 1=1 AND t.DEL_FLAG != 'Y' AND T.ENTITY_IDCODE=TT.ENTITY_IDCODE
AND t.AREA_ID like #{areaId}||'%'
AND tt.enterprise_name like '%'||#{queryCon}||'%'
AND t.TYPE =#{type}
AND t.COMPLAINT_TITLE =#{complaintTitle}
AND t.STATUS =#{status}
order by COMPLAINT_TIME DESC )f) WHERE rn BETWEEN 1+#{pageSize}*(#{pageNum}-1) AND #{pageSize}*#{pageNum}

SELECT COUNT(*) FROM TTS_SCLTXXCJ_COMPLAINT_V2 t,TTS_SCLTXXCJ_REGISTER_V2 tt
WHERE 1=1 AND t.DEL_FLAG != 'Y' AND T.ENTITY_IDCODE=TT.ENTITY_IDCODE

    AND t.AREA_ID like #{areaId}||'%'


    AND tt.enterprise_name like '%'||#{queryCon}||'%'



    AND t.COMPLAINT_TITLE =#{complaintTitle}


    AND t.TYPE =#{type}


    AND t.STATUS =#{status}
