mapper.update:
update TTS_SCLTXXCJ_CUSTOMER T
set T.DELFLAG = 'Y'
WHERE T.ID = #{id}

mapper.insert:
 insert into TTS_SCLTXXCJ_CUSTOMER (ID
 , USER_ID_CODE
 , CUSTOMER_ENTITY_ID_CODE
 , CUSTOMER_USER_ID_CODE
 , IP_ADDRESS
 , DELFLAG
 , CREATEBY
 , UPDATEBY
 , UPDATETIME
 , NAME
 , TYPE
 , ORGANIZATION_TYPE
 , LEGAL_REPRESENTATIVE
 , USER_NAME
 , ID_CARD_NO
 , PHONE
 , EMAIL
 , ADDRESS
 , STATUS
 , CREATETIME
 , ENTITY_ID_CODE
	)
 values (#{id,jdbcType=VARCHAR}
 , #{userIdCode,jdbcType=VARCHAR}
 , #{customerEntityIdCode,jdbcType=VARCHAR}
 , #{customerUserIdCode,jdbcType=VARCHAR}
 , #{ipAddress,jdbcType=VARCHAR}
 , #{delflag,jdbcType=VARCHAR}
 , #{createby,jdbcType=VARCHAR}
 , #{updateby,jdbcType=TIMESTAMP}
 , #{updatetime,jdbcType=TIMESTAMP}
 , #{name,jdbcType=VARCHAR}
 , #{type,jdbcType=VARCHAR}
 , #{organizationType,jdbcType=VARCHAR}
 , #{legalRepresentative,jdbcType=VARCHAR}
 , #{userName,jdbcType=VARCHAR}
 , #{idCardNo,jdbcType=VARCHAR}
 , #{phone,jdbcType=VARCHAR}
 , #{email,jdbcType=VARCHAR}
 , #{address,jdbcType=VARCHAR}
 , #{status,jdbcType=VARCHAR}
 , #{createtime,jdbcType=TIMESTAMP}
 , #{entityIdCode,jdbcType=VARCHAR}
   )
