mapper.select:
SELECT Tab.*
FROM (
SELECT T.*,ROW_NUMBER() OVER(ORDER BY T.CREATE_TIME DESC) AS NUM from TTS_SCLTXXCJ_PRODUCT_TYPE T
where 1 = 1 AND T.DEL_FLAG != 'Y'  AND T.ENTITY_IDCODE = #{user.entityIdcode}
)Tab
WHERE Tab.NUM BETWEEN 1+#{pageSize}*(#{pageNum}-1) AND #{pageSize}*#{pageNum}
ORDER BY Tab.CREATE_TIME DESC

mapper.update:
update TTS_SCLTXXCJ_PRODUCT_TYPE
set STATUS = #{status,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}
